import { React, useState } from 'react';
import Barra from '../Navbar/Navbar';
import './Registrase.css'
import { Form, Col, InputGroup, Button } from 'react-bootstrap';
import Footer from '../Footer/Footer';

function Registrarse() {
    const [validated, setValidated] = useState(false);
  
    const handleSubmit = (event) => {
      const form = event.currentTarget;
      if (form.checkValidity() === false) {
        event.preventDefault();
        event.stopPropagation();
      }
  
      setValidated(true);
    };
  
    return (
        <div>
            <Barra />
            <div>
      <Form noValidate validated={validated} onSubmit={handleSubmit}>
          <Form.Group as={Col} md="12" controlId="validationCustomUsername">
            <Form.Label>Correo</Form.Label>
            <InputGroup hasValidation>
              <Form.Control
                type="email"
                placeholder="email"
                aria-describedby="inputGroupPrepend"
                required
              />
              <Form.Control.Feedback type="invalid">
                Por favor digite un correo electronico valido.
              </Form.Control.Feedback>
            </InputGroup>
          </Form.Group>
          <Form.Group as={Col} md="12" controlId="validationCustom03">
            <Form.Label>Contraseña</Form.Label>
            <Form.Control type="password" placeholder="password" required />
            <Form.Control.Feedback type="invalid">
            Por favor, introduce una contraseña.
            </Form.Control.Feedback>
          </Form.Group>
          
          <Form.Check
            label="Quiero recibir información y promociones de la tienda."   
          />
          <Form.Check
            required
            label="Soy mayor de 18 años."
            feedback="Debe ser mayor de edad."
            feedbackType="invalid"
          /><Form.Check
            label="Recordarme."
          />
          <Button variant="outline-secondary" type="submit">Registrarme</Button>{' '}
        <Form.Check
            required
            label="He leído y acepto los terminos y condiciones de SportStore."
            feedback="Debe aceptar nuestros terminos y condiciones."
            feedbackType="invalid"
          />
      </Form>
      </div>
      <Footer />
      </div>
    );
  }

export default Registrarse;
