import { React, useState } from 'react';
import './InicioSesion.css';
import Barra from '../Navbar/Navbar';
import Footer from '../Footer/Footer';
import { Form, Button, Col, Row, Container, InputGroup } from 'react-bootstrap';

export const InicioSesion = () => {

    const [validated, setValidated] = useState(false);
  
    const handleSubmit = (event) => {
      const form = event.currentTarget;
      if (form.checkValidity() === false) {
        event.preventDefault();
        event.stopPropagation();
      }
  
      setValidated(true);
    };

  return (
    <div>
    <Barra />
    <div className='FondoInicioSe'>
            <Container>
                <Row>
                    <Col sm={6}>
                    <h1>INICIAR SESI√ìN</h1>
        <Form>
        <Form.Group as={Col} md="4" controlId="validationCustomUsername">
          <Form.Label>Correo</Form.Label>
          <InputGroup hasValidation>
            <Form.Control
              type="email"
              placeholder=""
              aria-describedby="inputGroupPrepend"
              required
            />
            <Form.Control.Feedback type="invalid">
              Por favor digite un correo valido.
            </Form.Control.Feedback>
          </InputGroup>
        </Form.Group>

        <Form.Group as={Col} md="6" controlId="validationCustom03">
          <Form.Label>Contrase√±a</Form.Label>
          <Form.Control type="pasword" placeholder="" required />
          <Form.Control.Feedback type="invalid">
            Por favor digite su contrase√±a.
          </Form.Control.Feedback>
        </Form.Group>
        
  <Form.Group className="mb-3" controlId="formBasicCheckbox">
    <Form.Check type="checkbox" label="Recordarme" />
  </Form.Group>
  <Button variant="outline-secondary" type="submit">Ingresar</Button>{' '}
  <Form.Group className="mb-3" controlId="formBasicCheckbox">
    <Form.Check type="checkbox" label="Acepto terminos y condiciones." />
  </Form.Group>
  <a href="/OlvidoContrasena">¬øOlvido su contrase√±a?</a>
</Form>
</Col>
<Col sm={6}>
    <h1>REGISTRATE Y DISFRUTA DE LOS BENEFICIOS QUE TIENE.</h1>
    <p>Recibe notificaciones de nuevos productos.</p>
    <p>Recibe ofertas especiales.</p>
    <p className='ultParrafo'>Cupon de 50% por ser nuevo usuario.</p>
    <Button variant="outline-secondary">Registrarme</Button>{' '}
</Col>
</Row>
</Container>
</div>

<Footer />
    </div>
  );
};

export default InicioSesion;
